INSERT INTO `proc` VALUES ('dangqun','strAt','FUNCTION','strAt','SQL','NO_SQL','NO','DEFINER','str nvarchar(1024) , isel INT','varchar(128) CHARSET utf8','begin\r\n\r\n/*\r\n\r\n	Copyright(C) 2014 凌绍清 All rights reserved.\r\n\r\n	描述:\r\n\r\n		由用户提供以字符\'|\'分隔的字符串,并提供要提取的区域,此函数以\'|\'符号作为\r\n\r\n	区分字段的分隔符,返回提取的字符串\r\n\r\n	入口参数:\r\n\r\n		str varchar(1024) 由用户提供以字符\'|\'分隔的字符串\r\n\r\n		iSel INT 	要提取的字符串下标,下标编号从1开始\r\n	\r\n	返回：\r\n		\r\n		指定区域的字符串\r\n	\r\n\r\n	作者:LingShaoQing\r\n\r\n	日期:2014/09/28\r\n\r\n*/\r\n\r\n	declare idx int;\r\n\r\n	declare idx2 int;\r\n\r\n	declare icount int;\r\n\r\n	declare iGetLen int default 0;\r\n\r\n	declare szTmp varchar(256) charset utf8 ;\r\n\r\n	declare szSrc,szConstBlank varchar(1024) charset utf8 ;\r\n\r\n	declare iLen int default 0;\r\n\r\n	declare chrSpec varchar(1) default \"|\";\r\n\r\n	declare iPos int;\r\n\r\n	DECLARE EXP_Error INT DEFAULT 0;\r\n	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION  Set EXP_ERROR=1;\r\n\r\n	SET szConstBlank=\"\";\r\n	\r\n	set szSrc=str;\r\n\r\n	set iLen= length(str);\r\n\r\n	set iPos=1;\r\n\r\n	set idx=iSel; \r\n\r\n	set icount=1;\r\n	\r\n\r\n\r\n	Lab_FS:Loop\r\n\r\n		\r\n\r\n		set idx=locate(chrSpec,str,iPos);\r\n\r\n\r\n\r\n		if iCount = iSel  then -- 找到匹配的区域\r\n\r\n\r\n\r\n			if idx !=0  then\r\n\r\n				set iGetLen=idx-iPos;\r\n\r\n			else \r\n\r\n				set iGetLen=ilen-iPos+1;\r\n\r\n			end if;\r\n\r\n			IF iGetLen = 0 THEN\r\n				SET szTmp=szconstblank;\r\n			ELSE\r\n				set szTmp=Mid(szSrc,iPos,iGetLen);\r\n			END IF;\r\n\r\n			leave Lab_FS;\r\n\r\n		\r\n\r\n		elseif idx =0 then -- 指定的区域超出范围,直接退出\r\n\r\n\r\n\r\n			set szTmp=szConstBlank;\r\n\r\n			leave Lab_fs;\r\n\r\n		end if;\r\n\r\n		\r\n\r\n		set icount=icount+1;\r\n\r\n		set iPos=idx+1;\r\n\r\n		 \r\n\r\n	end Loop LaB_FS;\r\n\r\n	\r\n	IF EXP_Error = 1 THEN\r\n		RETURN szConstBlank;\r\n	ELSE\r\n		return szTmp;\r\n	END IF;\r\n\r\n\r\n\r\nend','lingsq@%','2014-11-13 13:36:17','2014-11-13 13:36:17','','','utf8','utf8_general_ci','utf8_general_ci','begin\r\n\r\n/*\r\n\r\n	Copyright(C) 2014 凌绍清 All rights reserved.\r\n\r\n	描述:\r\n\r\n		由用户提供以字符\'|\'分隔的字符串,并提供要提取的区域,此函数以\'|\'符号作为\r\n\r\n	区分字段的分隔符,返回提取的字符串\r\n\r\n	入口参数:\r\n\r\n		str varchar(1024) 由用户提供以字符\'|\'分隔的字符串\r\n\r\n		iSel INT 	要提取的字符串下标,下标编号从1开始\r\n	\r\n	返回：\r\n		\r\n		指定区域的字符串\r\n	\r\n\r\n	作者:LingShaoQing\r\n\r\n	日期:2014/09/28\r\n\r\n*/\r\n\r\n	declare idx int;\r\n\r\n	declare idx2 int;\r\n\r\n	declare icount int;\r\n\r\n	declare iGetLen int default 0;\r\n\r\n	declare szTmp varchar(256) charset utf8 ;\r\n\r\n	declare szSrc,szConstBlank varchar(1024) charset utf8 ;\r\n\r\n	declare iLen int default 0;\r\n\r\n	declare chrSpec varchar(1) default \"|\";\r\n\r\n	declare iPos int;\r\n\r\n	DECLARE EXP_Error INT DEFAULT 0;\r\n	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION  Set EXP_ERROR=1;\r\n\r\n	SET szConstBlank=\"\";\r\n	\r\n	set szSrc=str;\r\n\r\n	set iLen= length(str);\r\n\r\n	set iPos=1;\r\n\r\n	set idx=iSel; \r\n\r\n	set icount=1;\r\n	\r\n\r\n\r\n	Lab_FS:Loop\r\n\r\n		\r\n\r\n		set idx=locate(chrSpec,str,iPos);\r\n\r\n\r\n\r\n		if iCount = iSel  then -- 找到匹配的区域\r\n\r\n\r\n\r\n			if idx !=0  then\r\n\r\n				set iGetLen=idx-iPos;\r\n\r\n			else \r\n\r\n				set iGetLen=ilen-iPos+1;\r\n\r\n			end if;\r\n\r\n			IF iGetLen = 0 THEN\r\n				SET szTmp=szconstblank;\r\n			ELSE\r\n				set szTmp=Mid(szSrc,iPos,iGetLen);\r\n			END IF;\r\n\r\n			leave Lab_FS;\r\n\r\n		\r\n\r\n		elseif idx =0 then -- 指定的区域超出范围,直接退出\r\n\r\n\r\n\r\n			set szTmp=szConstBlank;\r\n\r\n			leave Lab_fs;\r\n\r\n		end if;\r\n\r\n		\r\n\r\n		set icount=icount+1;\r\n\r\n		set iPos=idx+1;\r\n\r\n		 \r\n\r\n	end Loop LaB_FS;\r\n\r\n	\r\n	IF EXP_Error = 1 THEN\r\n		RETURN szConstBlank;\r\n	ELSE\r\n		return szTmp;\r\n	END IF;\r\n\r\n\r\n\r\nend'),('dangqun','prc_test','PROCEDURE','prc_test','SQL','READS_SQL_DATA','NO','DEFINER','in parin varchar(1024) charset gbk,out szret  int','','begin\r\n	/*	Copyright(C) 2014,凌绍清 All rights reserved.\r\n\r\n		summary:test\r\n	*\r\n	*	完成日期:2014/09/26\r\n	*	作者:凌绍清\r\n\r\n\r\n